for k in dir(OpenAI):
    v = getattr(OpenAI, k)
    if callable(v) and hasattr(v, "retry") and hasattr(v.retry, "wait"):
        v.retry.wait = wait_none()


for name, logger in logging.root.manager.loggerDict.items():
    if name.startswith("openinference.") and isinstance(logger, logging.Logger):
        logger.setLevel(logging.DEBUG)
        logger.handlers.clear()
        logger.addHandler(logging.StreamHandler())

LLAMA_INDEX_VERSION = tuple(map(int, version("llama-index-core").split(".")[:3]))


@pytest.mark.skip(reason="TODO: figure out why multi-modal fails")
@pytest.mark.parametrize("hide_inputs", [False, True])
@pytest.mark.parametrize("hide_input_messages", [False, True])
@pytest.mark.parametrize("hide_input_images", [False, True])
@pytest.mark.parametrize("hide_input_text", [False, True])
@pytest.mark.parametrize("base64_image_max_length", [0, 100_000])
def test_chat_with_config_hiding_inputs(
    tracer_provider: trace_api.TracerProvider,
    respx_mock: MockRouter,
    in_memory_span_exporter: InMemorySpanExporter,
    base64_image_url: str,
    hide_inputs: bool,
    hide_input_messages: bool,
    hide_input_images: bool,
    hide_input_text: bool,
    base64_image_max_length: int,
) -> None:
    LlamaIndexInstrumentor().uninstrument()
    config = TraceConfig(
        hide_inputs=hide_inputs,
        hide_input_messages=hide_input_messages,
        hide_input_images=hide_input_images,
        hide_input_text=hide_input_text,
        base64_image_max_length=base64_image_max_length,
    )
    assert config.hide_inputs is hide_inputs
    assert config.hide_input_messages is hide_input_messages
    assert config.hide_input_images is hide_input_images
    assert config.hide_input_text is hide_input_text
    assert config.base64_image_max_length is base64_image_max_length
    LlamaIndexInstrumentor().instrument(tracer_provider=tracer_provider, config=config)
    n = 10  # number of concurrent queries
    image_documents = load_image_urls([base64_image_url])
    chat_prompt = "Describe the images as an alternative text"
    chat_msg = openai_utils.generate_openai_multi_modal_chat_message(
        prompt=chat_prompt,
        role="user",
        image_documents=image_documents,
    )
    messages = [chat_msg for _ in range(n)]
    answer = (
        "The image shows a scenic landscape with a wooden boardwalk pathway "
        "leading through a grassy field. The sky is clear and blue with some "
        "scattered clouds. There is greenery on either side of the walkway, "
        "including tall grasses and bushes, and trees in the background. "
        "This peaceful and open setting is suggestive of a nature reserve "
        "or park."
    )
    llm_model_name = "gpt-4o"
    llm = OpenAIMultiModal(
        model=llm_model_name,
        max_retries=0,
        timeout=0.01,
    )
    respx_kwargs: Dict[str, Any] = {
        "json": {
            "choices": [
                {
                    "index": 0,
                    "message": {"role": "assistant", "content": answer},
                    "finish_reason": "stop",
                }
            ],
        }
    }
    url = "https://api.openai.com/v1/chat/completions"
    respx_mock.post(url).mock(return_value=Response(status_code=200, **respx_kwargs))

    def chat(message: ChatMessage) -> None:
        _ = llm.chat([message])

    def main() -> None:
        with ThreadPoolExecutor() as executor:
            for message in messages:
                executor.submit(copy_context().run, partial(chat, message))

    with suppress(openai.BadRequestError):
        main()

    spans = in_memory_span_exporter.get_finished_spans()
    traces: DefaultDict[int, Dict[str, ReadableSpan]] = defaultdict(dict)
    for span in spans:
        traces[span.context.trace_id][span.name] = span

    assert len(traces) == n
    for spans_by_name in traces.values():
        name = "OpenAIMultiModal.chat"
        span = spans_by_name.pop(name)
        assert span is not None
        assert span.parent is None
        attributes = dict(span.attributes or {})
        assert attributes.pop(OPENINFERENCE_SPAN_KIND, None) == LLM.value
        assert attributes.pop(LLM_MODEL_NAME, None) == llm_model_name
        assert attributes.pop(LLM_INVOCATION_PARAMETERS, None) is not None
        # Input value
        input_value = attributes.pop(INPUT_VALUE, None)
        assert input_value is not None
        if hide_inputs:
            assert input_value == REDACTED_VALUE
        else:
            assert attributes.pop(INPUT_MIME_TYPE, None) == "application/json"
            input_value = json.loads(cast(str, input_value))
            input_messages = input_value.pop("messages")  # type:ignore
            assert len(input_messages) == 1
            input_message = input_messages[0]
            assert input_message.pop("role") == messages[0].role.value
            assert input_message.pop("content") == messages[0].content
            assert input_message.pop("additional_kwargs") == messages[0].additional_kwargs
            assert input_message == {}

        # Input messages
        if not hide_inputs and not hide_input_messages:
            _check_llm_message(
                LLM_INPUT_MESSAGES,
                0,
                attributes,
                chat_msg,
                hide_text=hide_input_text,
                hide_images=hide_input_images,
                image_limit=base64_image_max_length,
            )
        # Output value
        assert span.status.status_code == trace_api.StatusCode.OK
        assert not span.status.description
        output_value = cast(Optional[str], attributes.pop(OUTPUT_VALUE, None))
        assert output_value == f"assistant: {answer}"

        # Output messages
        _check_llm_message(
            LLM_OUTPUT_MESSAGES,
            0,
            attributes,
            ChatMessage(
                role=MessageRole.ASSISTANT,
                content=answer,
            ),
        )
        assert attributes == {}


